sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    //Account Data
    var results = api.data.get_all_accounts.results;

    //Clear Account, if Account DB is empty
    if(results == undefined || results.length == 0){
        api.setState('account',{'id':'-1','label':''});
        api.setState('account_query', '');
    } 
}
