sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  return api.context.session.user.roles.indexOf("sn_ind_tmt_orm.order_fulfilment_agent") != -1 || api.context.session.user.roles.indexOf("admin") != -1 || api.context.session.user.roles.indexOf("maint") != -1;
}
