sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api}) {
  return api.state.category == null || api.state.category == '' || api.state.orderType == null || api.state.orderType == '';
}
