sys_script_include.script:
var GetProductNumber = Class.create();
GetProductNumber.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getProduct: function(oli) {

        var prodGr = new GlideRecord('sn_ind_tmt_orm_product_order');
        prodGr.addQuery('order_line_item', oli);
        prodGr.query();

        if (prodGr) {
			gs.info('prodGr.sys_id : ' +prodGr.sys_id);
            return prodGr.sys_id;
        }

    },
    type: 'GetProductNumber'
});
