sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    if (api.context.props.type == "add") {
        if(api.state.inlineEdit == "true"){
            return "characteristic,characteristic_option_value, characteristic_option, domain_order";
        }
        return "characteristic, characteristic_option_value, domain_order";
    } else {
        if(api.state.inlineEdit == "true"){
            return "characteristic,previous_characteristic_option_value,characteristic_option_value, domain_order";
        }
        return "characteristic,previous_characteristic_option_value, characteristic_option_value, domain_order";
    }

}
