sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    switch (event.elementId) {
        case 'btn_configure_items':
            //call create oli
            if (api.context.props.category === 'product') {
                api.data.create_order_line_items_db.execute({
                    "orderSysId": api.context.props.sysId,
                    "data": api.state.locationsOrders
                });

                var route = 'configure';
                var fields = {
                    'sysId': api.context.props.sysId,
                    'category': api.context.props.category,
                    'type': api.context.props.type,
                };

                var payload = {
                    route: route,
                    fields: fields
                };

                api.emit('SN_NAV_ATT_CONFIGURE_54321', payload);

            } else if (api.context.props.category === 'service') {
                const newdata = JSON.parse(JSON.stringify(api.state.locationsOrders).split('"offerings":').join('"specifications":'));
                api.data.create_mandatory_service_order_line_items_db.execute({
                    "orderSysId": api.context.props.sysId,
                    "data": newdata
                });
            }
            break;
    }

}
