sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        NO_LOCATION,
        ORDER_TYPE,
        ORDER_LINE_ITEM_ACTION
    } = imports["sn_app_orm_wksp.Constants"]();
    var orderLineItemObj = api.data.get_order_line_item_db.output.data.GlideRecord_Query.sn_ind_tmt_orm_order_line_item._results[0];
    var location = orderLineItemObj.location;
    var orderType = orderLineItemObj.order_category.value;

    api.setState("location", {
        "id": location.value,
        "label": (location.displayValue == null || location.displayValue == "") ? NO_LOCATION : location.displayValue
    });
    if (orderType == ORDER_TYPE.PRODUCT) {
        var productOffering = orderLineItemObj.product_offering;
        api.setState("productOffering", {
            "id": productOffering.value,
            "label": productOffering.displayValue
        });
        var orderLineItemAction = orderLineItemObj.action.value;
        if (orderLineItemAction == ORDER_LINE_ITEM_ACTION.CHANGE) {
            var serviceSpec = orderLineItemObj.specification;
            api.setState("specification", {
                "id": serviceSpec.value,
                "label": serviceSpec.displayValue
            });
        }
    }

    if (orderType == ORDER_TYPE.SERVICE) {
        var serviceSpec = orderLineItemObj.specification;
        api.setState("specification", {
            "id": serviceSpec.value,
            "label": serviceSpec.displayValue
        });
    }

    // Get all order line items
    api.data.get_all_order_line_items.refresh();
}
