sys_ux_client_script.script:
function handler({
    api,
    helpers,
    event,
    imports
}) {
    var payload = {
        route: 'record',
        targetRoute: {
            route: 'record',
            fields: {
                table: event.payload.fields.table,
                sysId: event.payload.fields.sysId
            }
        },
        fields: {
            table: event.payload.fields.table,
            sysId: event.payload.fields.sysId
        },
        redirect: false,
        passiveNavigation: null,
        title: '',
        multiInstField: '',
        external: ''
    };
    api.emit('NAV_ITEM_SELECTED', payload);
}
