sys_ux_client_script.script:
        /**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {

    if(event.elementId == 'validate_related_items_db') {
        var msg = api.data.validate_related_items_db.output;

        if (msg != "") {
            var alerts = [{
                id: "compt-rule-info",
                status: "info",
                icon: "info-circle-outline",
                content: msg,
                action: {
                    type: "dismiss"
                }
            }];

            api.emit('NOW_UXF_PAGE#ADD_NOTIFICATIONS', {
                items: alerts
            });
            return false;
        }
    }else {
        api.data.validate_related_items_db.refresh();
    }  

}
