sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var specTreeData = api.data.get_optional_specification_hierarchy_db.output;
    api.setState("specTreeData", specTreeData);
    var expandedSpecs = [];
    specTreeData.forEach(function(location, index) {
        expandedSpecs.push([location.id]);
        if (location.children.length != 0) {
            for(var i=0;i<location.children.length;i++)
                expandedSpecs.push([location.id, location.children[i].id]);
        }
    });
    api.setState("expandedSpecifications", expandedSpecs);


    // below code will handle the optionla line item count update as wells as update disableAddLineItem state to show / hide the add line item button
    var optionalCount = 0;
    for (var loc = 0; loc < specTreeData.length; loc++) {
        for (var off = 0; off < specTreeData[loc].children.length; off++) {
            if(isOptionalLineItem(specTreeData[loc].children[off]))
                optionalCount ++;
        }
    }

    var countMsg = "Order line items" + " (" + optionalCount + ")";
    api.setState("optionalLineItemCount", countMsg);
    if (optionalCount) {
        api.setState("disableAddLineItem", false);
    } else {
        api.setState("disableAddLineItem", true);
    }

    function isOptionalLineItem(node) {
        if (node.length == 0) {
            return false;
        } else {
            for (var j = 0; j < node.children.length; j++) {
                if (node.children[j].mandatory == "false") {
                    return true;
                }
                isOptionalLineItem(node.children[j].children);
            }
        }
        return false;
    }
}
