sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        getStepperData,
        getFlatternLineItemsAndTreeData
    } = imports["sn_app_orm_wksp.orderCreationUtils"]();
    const {
        ORDER_ACTION
    } = imports["sn_app_orm_wksp.Constants"]();
    const treeData = api.data.get_order_line_item_tree_data.output;

    const ali = getActiveLineItem(treeData, api.state.activeLineItem);

    const treeMetaData = getFlatternLineItemsAndTreeData(treeData, ali);
    if (ali === null) {
        api.setState("expandedLineItems", treeMetaData.expandedLineItems);
        api.setState("selectedLineItems", treeMetaData.selectedLineItems);
        api.setState("activeLineItem", treeMetaData.activeLineItem);

        if (treeData.length == 0 || (api.context.props.type === ORDER_ACTION.ADD && treeMetaData.activeLineItem.mandatory)) {
            api.setState("isDeleteDisabled", true);
        } else {
            api.setState("isDeleteDisabled", false);
        }
    }
    api.setState("treeData", treeData);
    var count = `Order line items ${'('}${treeMetaData.lineItems.length}${')'}`;
    api.setState("lineItemCount", count);
    api.setState("orderLineItems", treeMetaData.lineItems);


    if (isErrorInLineItems(treeData)) {
        api.setState("enableDisableReviewButton", true);
    } else {
        api.setState("enableDisableReviewButton", false);
    }

    function isErrorInLineItems(treeData) {
        if (treeData.length == 0) {
            return true;
        }
        for (var loc = 0; loc < treeData.length; loc++) {
            for (var off = 0; off < treeData[loc].children.length; off++) {
                if (!verifyChildLineItems(treeData[loc].children[off])) {
                    return true;
                }
            }
        }
        return false;
    }

    function verifyChildLineItems(node) {
        // if status is busy, return false;
        if (node.highlightedValueProps && node.highlightedValueProps[0].status == "critical")
            return false;

        // element not found add this tree node then go futher for its children
        if (node.children)
            for (var j = 0; j < node.children.length; j++) {
                if (!verifyChildLineItems(node.children[j]))
                    return false;
            }
        return true;
    }

    function getActiveLineItem(treeData, activeLineItem) {
        if (activeLineItem == null) {
            return null;
        }
        var queue = [];
        queue = queue.concat(treeData);
        while (queue.length > 0) {
            var data = queue.pop();
            if (activeLineItem.id === data.id) {
                return activeLineItem;
            }
            queue = queue.concat(data.children);
        }
        return null;
    }
}
