/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    api.setState('modalTable', event.payload.table);
    api.setState('modalSysId', event.payload.sys_id);
var object = {"table": api.state.modalTable,"sysId": api.state.modalSysId};
//var object = {"table":"event.payload.table","sysId":"event.payload.sys_id"};
api.setState('testParam', object);
}
