sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
        var route = 'select';
        var fields = {
                    'sysId':api.context.props.sysId,
                    'category':api.context.props.category,
                    'type':api.context.props.type,
                    'orderNumber':api.state.orderNumber
                    };

        var payload = {
                route: route,
                fields: fields
            };

api.setState('payload', payload);

console.log('payload : ' + JSON.stringify(payload));
        api.emit('NAVIGATE_TO_STEP_CREATE_ATT_TEST', api.state.payload);
}
