sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    const currentItems = api.state.currentItems;
    var items = [];
    var records;
    if (api.context.props.category === 'product') {
        records = api.data.get_product_offerings_db.results;
    } else {
        records = api.data.get_all_cfs_specifications_db.results;
    }
    if (records === undefined)
        return;
    let selectedItems = [];
    for (var ind = 0; ind < currentItems.length; ind++) {
        for (var off in currentItems[ind].offerings) {
            selectedItems.push(currentItems[ind].offerings[off].id);
        }
    }

    for (var indRec = 0; indRec < records.length; indRec++) {
        if (selectedItems.indexOf(records[indRec]._row_data.uniqueValue) == -1) {
            items.push({
                id: records[indRec]._row_data.uniqueValue,
                label: records[indRec]._row_data.displayValue
            });
        }
    }
    return items;
}
