sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    const stateVal = event.payload.data.output.data.GlideRecord_Mutation.update_sn_ind_tmt_orm_order.state.value;
    api.setState("orderStatus", event.payload.data.output.data.GlideRecord_Mutation.update_sn_ind_tmt_orm_order.state.value);
    if(stateVal === "new") {
        helpers.modal.open("order_success_modal");
    }    
}
