sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    const {
        NO_LOCATION,
        ORDER_TYPE,
        ORDER_ACTION
    } = imports["sn_app_orm_wksp.Constants"]();

    if (event.elementId == "copy_and_paste_selected_order_line_item_configurations") {
        api.data.get_order_line_item_tree_data.refresh();
        helpers.modal.close("copy_configuration", {
            "bare": true
        });
        api.emit('NOW_UXF_PAGE#ADD_NOTIFICATIONS', {
            items: [{
                id: 'id1',
                status: 'positive',
                icon: 'circle-check-outline',
                content: 'You have successfully copied configurations',
                action: {
                    type: 'dismiss'
                }
            }]
        });
    } else {
        api.setState('selectedLocations', []);
        var locations = getLocationsForCopyConfig();
        api.setState('locationDropDown', locations);
        helpers.modal.open("copy_configuration", {
            "bare": true
        });
    }

    function getLocationsForCopyConfig() {
        if (api.data.get_all_order_line_items.output) {
            var orderItems = api.data.get_all_order_line_items.output.data.GlideRecord_Query.sn_ind_tmt_orm_order_line_item._results;
            var allItemLocations = [];
            var locations = [];

            for (var i = 0; i < orderItems.length; i++) {
                if (!locations.includes(orderItems[i].location.value)) {
                    //For new product orders, filter location based on offering else filter locations based on specification
                    if (api.context.props.type == ORDER_ACTION.ADD && api.context.props.category == ORDER_TYPE.PRODUCT) {
                        if (api.state.productOffering.id == orderItems[i].product_offering.value) {
                            locations.push(orderItems[i].location.value);
                            allItemLocations.push({
                                id: orderItems[i].location.value,
                                label: (orderItems[i].location.displayValue == null || orderItems[i].location.displayValue == "") ? NO_LOCATION : orderItems[i].location.displayValue
                            });
                        }
                    } else if (api.state.specification.id == orderItems[i].specification.value) {
                        locations.push(orderItems[i].location.value);
                        allItemLocations.push({
                            id: orderItems[i].location.value,
                            label: (orderItems[i].location.displayValue == null || orderItems[i].location.displayValue == "") ? NO_LOCATION : orderItems[i].location.displayValue
                        });
                    }
                }
            }
            return allItemLocations;
        }
    }
}
