sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    // if no record found then keep contact form empty
    if(api.data.look_up_records_contact_db.results.length == 0){
        api.setState('firstName', "");
        api.setState('lastName', "");
        api.setState('email', "");
        api.setState('contactNum', "");
        return;
    }
    // update contact form 

    var contact = api.data.look_up_records_contact_db.results[0];
    api.setState('firstName', contact.first_name.value);
    api.setState('lastName', contact.last_name.value);
    api.setState('email', contact.email.value);
    api.setState('contactNum', contact.business_phone.value);
}
