sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const {
        PUBLISHED
    } = imports["sn_app_orm_wksp.Constants"]();
    if (api.state.activeLocation) {
        const activeLocationId = api.state.activeLocation.id;
        const locOrders = api.state.locationsOrders;
        const currentItems = locOrders[activeLocationId].ordersData;
        const index = event.context.item.index;
        const enteredValue = event.payload.value;
        if(event.elementId === "offerings_selections"){
            var offeringsSelectionArr = [];
            offeringsSelectionArr.push(event.payload.item);
            currentItems[index].offerings = offeringsSelectionArr;

            // Reset the filter value
            var offeringFilter = api.state.offeringFilter;
            offeringFilter.value = PUBLISHED;
            api.setState('offeringFilter', offeringFilter);
        }
        else if(event.elementId === "inp_quantity")
            currentItems[index].quantity = enteredValue;
        api.setState("currentItems", currentItems);
        const updated = {
            ...locOrders,
            [activeLocationId]: {
                ...locOrders[activeLocationId],
                'ordersData': [...currentItems]
            }
        };
        api.setState("locationsOrders", updated);
    }
}
