sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    api.data.copy_and_paste_selected_order_line_item_configurations.execute({
        "selectedLocations": api.state.selectedLocations,
        "orderId": api.context.props.sysId,
        "sourceLineItemId": api.state.activeLineItem.id
    });
}
