sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({
    api,
    event,
    helpers,
    imports
}) {
    if (api.context.session.user.roles.indexOf("sn_ind_tmt_orm.service_order_agent") == -1) {
        api.setState("category", "product");
        api.setState("type", "add");
    } else {
        api.setState("category", "service");
    }
    api.setState("orderType", "add");
}
