sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    var locations = api.data.get_locations_for_account_custom_db.output;
    const selectedLocations = api.state.selectedLocations;
    const filteredLocations = locations.filter(loc => !selectedLocations.some(sloc => loc.id === sloc.id));
    api.setState("filteredLocations", filteredLocations);
}
