sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {

    const activeLocationId = api.state.activeLocation.id;
    const locOrders = api.state.locationsOrders;
    const currentContact = locOrders[activeLocationId].contact;
    const enteredData = event.payload.value;
    switch (event.elementId) {
        case "inp_first_name":
            currentContact.firstName = enteredData;
            break;
        case "inp_last_name":
            currentContact.lastName = enteredData;
            break;
        case "inp_email":
            currentContact.email = enteredData;
            break;
        case "inp_mobile_number":
            currentContact.phone = enteredData;
            break;
    }

    const updated = {
        ...locOrders,
        [activeLocationId]: {
            ...locOrders[activeLocationId],
            'contact': currentContact
        }
    };
    api.setState("locationsOrders", updated);
}
