sys_ux_data_broker_transform.script:
function transform(input) {

//gs.info('input : ' + JSON.stringify(input));
	customerFacingDetailsSysId = input.recordSysId;

	gs.info('customerFacingDetailsSysId :' + customerFacingDetailsSysId);

	var customerFacingGr = new GlideRecord('x_876519_sd_voice_customer_facing_details');
	customerFacingGr.addQuery('sys_id', customerFacingDetailsSysId);
	customerFacingGr.query();

	if(customerFacingGr.next()){
		var privateRoutingGr = new GlideRecord('x_876519_att_work_private_routing_details');
		privateRoutingGr.addQuery('u_domain_order', customerFacingGr.u_domain_order);
		privateRoutingGr.query();

		if (privateRoutingGr.next()){
			var output = privateRoutingGr.getUniqueValue();
		}
	}
	gs.info('output : ' + output);
	gs.info('output type: ' + typeof output);
	return output;
}
